div.script:
jQuery(function(){
    jQuery('#search-autocomplete').autocomplete({
        source: function( request, response ) {
            jQuery.getJSON("//www.lb.lt/lt/paieska/autocomplete?term=" + request.term,
                function(data) { response(data); } );
        },
        messages: {
            noResults: '',
            results: function() {}
        },
        minLength: 3,
        appendTo: '#search-autocomplete-top-result' ,
        select: function (event, ui) {
            jQuery(this).val(ui.item.value);
            jQuery(this).parents('form').submit();
            return false;
        }
    });
    jQuery('.main_search_submit').click(function(){
        jQuery(this).parents('form').submit();
    });
});

var pendingFocus;

$(function() {
    $('#events_calendar').on('keyup', '.ui-datepicker-prev,.ui-datepicker-next', function(e){
        var key = (e.keyCode || e.which);
        //Enter
        if(key == 13) {
            pendingFocus = $(this).attr('class');
            $(this).trigger('click');
        }
        return false;
    });

    initPopower()
});

function initPopower()
{
    $('#events_calendar .event-day a').each(function(index, item){

        $(item).popover( {
            container: 'body',
            content: $(item).parent().find('.popover-content').html(),
            trigger: 'click',
            html: true,
            placement: 'top'
        } ).on("mouseenter", function () {
            var _this = this;

            $(_this).parent().addClass('active-popover');

            $(this).popover("show");
            $(".popover").on("mouseleave", function () {
                $(_this).popover('hide');
                $(_this).parent().removeClass('active-popover');
            });
        } ).on("mouseleave", function () {
            var _this = this;
            setTimeout(function () {
                if (!$(".popover:hover").length) {
                    $(_this).popover("hide");
                    $(_this).parent().removeClass('active-popover');
                }
            }, 100);
        } );
    } );
}

function changeEventsMonth(month)
{
    $('#calendar_loader').show();
    $.ajax({
        url: "//www.lb.lt/lt/renginiai/calendarajaxwidget?ajax=1&month=" + month,
        success: function (data) {
            var jCalendarWrap = $('#events_calendar');
            jCalendarWrap.html(data);
            initPopower();
            if(pendingFocus) {
                var className = '.' + pendingFocus.replace(' ', '.');
                jCalendarWrap.find(className).focus();
                pendingFocus = '';
            }
        }
    });
}

